import React from 'react';
import { useRef, useState } from 'react';

function Recherche() {

    const inputRef = useRef();
    const [records, setRecords] = useState(null);

    function onValidateForm(event){
        event.preventDefault();

        const searchValue = inputRef.current.value;

        fetch('https://opendata.paris.fr/api/v2/catalog/datasets/que-faire-a-paris-/records?search=theatre')
        .then(res => res.json())
        .then(data => {setItems(data.records)})
    }


    return (
        <div>
            <h1>Page de recherche</h1>

            <form onSubmit={onValidateForm}>
                <input type="text" ref={inputRef} />
                <button type="submit">Rechercher</button>
            </form>

            {records && <>
            {records.map(record => <p>{record.fields.title}</p>)}
            </>}
        </div>
    );
}

export default Recherche;
