import React, {Component} from 'react';

class App extends Component {

    constructor(props) {
        super(props);
        this.state= {
            items: [],
            isLoaded: false,
        }
    }

    componentDidMount() {

        fetch('https://opendata.paris.fr/api/v2/catalog/datasets/que-faire-a-paris-/records?sort=-date_start&limit=1&pretty=false&timezone=UTC')
            .then(res => res.json())
            .then(json => json.records.map(record => record.record))
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json,
                })
            });

    }

    render() {

        var { isLoaded, items } = this.state;

        if (!isLoaded) {
            return <div>Chargement...</div>;
        }

        else {

            return (
                <div className="App">
                    <ul>
                        {items.map(item => (
                            <li key={item.id}>
                                <div>
                                    <figure>
                                        <picture>
                                            <img src="{item.fields.cover.url}" alt="{item}"></img>
                                        </picture>
                                    </figure>
                                </div>
                                <h2>{item.fields.title}</h2>
                                <span>{item.fields.date_start}</span>
                                <p>{item.fields.lead_text}</p>
                            </li>
                        ))}
                    </ul>
                </div>
            );
        }

    }
}

export default App;
