import React, {useState} from 'react';
import {Link} from 'react-router-dom';

function Card(props) {

    const id = props.id;
    const yo = localStorage.getItem(id);

    const [isChecked, setIsChecked] = useState(false);

    const handleLike = (e) => {
        e.preventDefault();

        if(isChecked === false && id !== yo) {

          localStorage.setItem(id , id)
          setIsChecked(true)
        } else {
          localStorage.removeItem(id)
          setIsChecked(false)
        }

      }

    return (
        <Link to={"/evenement/"+props.id}>
            <div className="CardCustom">
                <div className="leftSideCard">
                    <img src={props.img} alt={props.alt}/>
                </div>
                <div className="rightSideCard">
                    <span>{props.date}</span>
                    <h2>{props.title}</h2>
                    <p>{props.desc}</p>

                    {isChecked ? (<button onClick={e => handleLike(e)}><img src='{'></img></button>) 
                        : ( <button onClick={e => handleLike(e)}>Unlike</button>)}

                </div>

            </div>
        </Link>
    );
}

export default Card;
